using System;  namespace add3.cs { 	class MainClass 	{ 		public static void Main (string[] args) 		{ 			Console.Write ( "Enter an integer: "); 			string xString = Console.ReadLine(); 			double x = double.Parse(xString); 			Console.Write( "Enter another integer: "); 			string yString = Console.ReadLine(); 			double y = double.Parse(yString); 			Console.Write( "Enter your last integer: "); 			string zString = Console.ReadLine(); 			double z = double.Parse(zString); 			double sum = x + y + z; 			Console.WriteLine("They add up to " + sum); 		} 	} }   