using System;  namespace quotient.cs { 	class MainClass 	{ 		static string QuotientString(int x, int y) 		{ 			int ans; 			int remain; 			ans = x / y; 			remain = x % y; 			return string.Format("The quotient of {0} and {1} is {2} with a remainder of {3}.", x, y, ans, remain);  		} 		static void Main () 		{ 			//test 1 			Console.WriteLine(QuotientString(10, 5));  			//test 2 			Console.WriteLine(QuotientString(2, 4));  			//test 3 			Console.WriteLine(QuotientString(100, 9));  			//user test 			Console.Write("Enter an integer: "); 			int a = int.Parse(Console.ReadLine()); 			Console.Write("Enter another integer: "); 			int b = int.Parse(Console.ReadLine()); 			Console.WriteLine(QuotientString(a, b)); 		} 	} }  