using System;  namespace IntroCS { 	class quotient 	{ 		static string QuotientString(int x, int y) 		{ 			int ans = x / y; 			int remain = x % y; 			string sentence = "The quotient of " + x + " and " + y + " is " + ans + " with a remainder of " + remain + "."; 			return sentence;  		} 		public static void Main() 		{ 			//test 1 			Console.WriteLine(QuotientString(10, 5));  			//test 2 			Console.WriteLine(QuotientString(2, 4));  			//test 3 			Console.WriteLine(QuotientString(100, 9));  			//user test 			int a = UIF.PromptInt("Enter an integer: ");      			int b = UIF.PromptInt("Enter another integer: ");  			Console.WriteLine(QuotientString(a, b)); 		} 	} }   